# 让自己的窗口跑起来# pip install -i https://pypi.tuna.tsinghua.edu.cn/simple xxxx # 打包命令 pyinstaller xxxxxxxx --onefile --icon=xxxxxxxxxxfrom PyQt5.Qt import *import sys#写一个简单的pyqt5程序，弹出一个窗口，显示hello worldclass Ui_MainWindow(object):#这个类的作用是实现一个窗口，这个窗口里面有一个按钮，按钮上面显示的文字是PushButton    def setupUi(self, MainWindow):    #上一行代码的意思是，这个类中有一个setupUi方法，这个方法的作用是实现一个窗口，mainwindow是这个窗口的标题        MainWindow.setObjectName("MainWindow")        MainWindow.resize(800, 600)        self.centralwidget = QtWidgets.QWidget(MainWindow)        #上一行代码的意思是，创建一个QWidget对象，这个对象是一个窗口的基本组件，也就是说，这个窗口是由很多个QWidget组成的        #centralwidget是什么意思呢？就是中心组件，也就是说，这个窗口的中心是由这个QWidget组成的        self.centralwidget.setObjectName("centralwidget")        self.pushButton = QtWidgets.QPushButton(self.centralwidget)        self.pushButton.setGeometry(QtCore.QRect(300, 240, 93, 28))        self.pushButton.setObjectName("pushButton")        MainWindow.setCentralWidget(self.centralwidget)        #上一行代码的意思是，把这个QWidget放到窗口的中心位置        self.retranslateUi(MainWindow)        #上一行代码的意思是，调用Ui_MainWindow类中的retranslateUi方法，具体功能是设置窗口的标题        QtCore.QMetaObject.connectSlotsByName(MainWindow)    def retranslateUi(self, MainWindow):        _translate = QtCore.QCoreApplication.translate        #上一行代码的意思是，创建一个翻译器，这个翻译器的作用是把英文翻译成中文，翻译器是Qt自带的，所以不需要我们自己写        MainWindow.setWindowTitle(_translate("MainWindow", "MainWindow"))        #上一行代码的意思是，设置窗口的标题        self.pushButton.setText(_translate("MainWindow", "PushButton"))        #上一行代码的意思是，设置按钮上面的文字class MyWindow(QtWidgets.QMainWindow, Ui_MainWindow):#这个类的作用是继承了QtWidgets.QMainWindow和Ui_MainWindow，功能是实现了一个窗口    def __init__(self, parent=None):#作用是初始化这个类        #self是指这个类本身，这个类的实例化对象        super(MyWindow, self).__init__(parent) #这行代码的意思是继承父类的属性和方法        self.setupUi(self)#这行代码的意思是调用Ui_MainWindow类中的setupUi方法        self.pushButton.clicked.connect(self.pushButton_clicked)        #上一行代码的意思是，当你点击pushButton按钮的时候，就会触发pushButton_clicked方法    def pushButton_clicked(self):        print("hello world")if __name__ == "__main__":    app = QtWidgets.QApplication(sys.argv)#创建一个QApplication，也就是你要开发的软件app    myshow = MyWindow()#/这行代码的意思是实例化你的主窗口    myshow.show()#这行代码的意思是显示这个主窗口    sys.exit(app.exec_())#不加这句，程序界面会一闪而过