# 请输入这个程序的功能 # pip install -i https://pypi.tuna.tsinghua.edu.cn/simple xxxx # 打包命令 pyinstaller xxxxxxxx --onefile --icon=xxxxxxxxxx #from PyQt5.Qt import *import sysclass Mywidget(QWidget):    def timerEvent(self, *args, **kwargs):        self.resize(self.width() + 10, self.height() + 10)app = QApplication(sys.argv)window = Mywidget()window.setWindowTitle("你好")window.resize(500, 500)window.startTimer(100)window.show()sys.exit(app.exec_())