# 鼠标悬停在按钮上时显示提示信息# pip install -i https://pypi.tuna.tsinghua.edu.cn/simple xxxx # 打包命令 pyinstaller xxxxxxxx --onefile --icon=xxxxxxxxxx #from PyQt5.QtWidgets import QAbstractButton, QApplication, QPushButtonclass MyButton(QPushButton):    def __init__(self, parent=None):#这行代码的意思是，如果没有传入parent参数，那么就默认为None        super().__init__(parent)        self.setToolTip("This is a custom button")#这行代码的意思是，设置鼠标悬停在按钮上时显示的提示信息，setToolTip()方法是QAbstractButton类的方法，功能是设置鼠标悬停在按钮上时显示的提示信息    def enterEvent(self, event):        self.setToolTip(self.toolTip())        super().enterEvent(event)#这行代码的意思是，调用父类的enterEvent()方法，这样就可以保证按钮的默认功能不受影响，参数的意思是，鼠标进入事件，传入的参数是QEvent类型的对象    def leaveEvent(self, event):        self.setToolTip("")        super().leaveEvent(event)if __name__ == "__main__":    import sys    app = QApplication(sys.argv)    button = MyButton()    button.show()    sys.exit(app.exec_())