# 设置按钮，电击按钮数字加1# 设置按钮图标# 设置按钮图标大小# 设置按钮快捷键# 设置按钮按住不放，会一直触发信号# 设置按钮按住不放的延迟时间，一直触发信号# 设置按钮按下状态# 设置单选按钮选中状态# 设置复选框选中状态# 设置全选按钮# 设置全不选按钮# 设置反选按钮# 设置radio被选中时，checkbox不可被选中# 设置checkbox被选中时，radio不可被选中# pip install -i https://pypi.tuna.tsinghua.edu.cn/simple xxxx # 打包命令 pyinstaller xxxxxxxx --onefile --icon=xxxxxxxxxx from PyQt5.Qt import *import sysapp = QApplication(sys.argv)window = QWidget()window.setWindowTitle("你好")window.resize(500, 500)btn = QPushButton(window)btn.setText("1")icon=QIcon("图标头像.ico")btn.setIcon(icon)btn.setIconSize(QSize(50,50))#QSize是一个类，不是函数，功能是设置图标大小，setQSize是一个函数，功能是设置窗口大小#设置按钮的快捷键btn.setShortcut("Alt+1")#设置按住按钮不放，会一直触发信号btn.setAutoRepeat(True)btn.setAutoRepeatDelay(1000)#设置按住按钮不放，会一直触发信号的延迟时间radiobutton=QRadioButton(window)radiobutton.setText("radio")radiobutton.move(100,100)checkbox=QCheckBox(window)checkbox.setText("checkbox")checkbox.move(100,200)# btn.setDown(True)#设置按钮按下状态# radiobutton.setChecked(True)#设置单选按钮选中状态# checkbox.setChecked(True)#设置复选框选中状态#设置一个全选按钮btn2=QPushButton(window)btn2.setText("全选")btn2.move(100,300)btn2.clicked.connect(lambda : radiobutton.setChecked(True))btn2.clicked.connect(lambda : checkbox.setChecked(True))#设置一个全不选按钮btn3=QPushButton(window)btn3.setText("全不选")btn3.move(200,300)btn3.clicked.connect(lambda : radiobutton.setChecked(False))btn3.clicked.connect(lambda : checkbox.setChecked(False))#设置一个反选按钮，不用匿名函数def reverse_1():    radiobutton.setChecked(not radiobutton.isChecked())#not是一个函数，功能是取反,radiobutton.isChecked(),是一个函数，功能是判断是否选中    checkbox.setChecked(not checkbox.isChecked())btn4=QPushButton(window)btn4.setText("反选1")btn4.move(300,300)btn4.clicked.connect(reverse_1)#设置一个反选按钮，用toogle函数def reverse_2():    radiobutton.toggle()#toggle函数是一个函数，功能是反选    checkbox.toggle()btn5=QPushButton(window)btn5.setText("反选2")btn5.move(400,300)btn5.clicked.connect(reverse_2)#radio被选中时，checkbox不可被选中def radio_checked():    checkbox.setEnabled(not radiobutton.isChecked())radiobutton.clicked.connect(radio_checked)#checkbox被选中时，radio不可被选中def checkbox_checked():    radiobutton.setEnabled(not checkbox.isChecked())checkbox.clicked.connect(checkbox_checked)#clicked是一个信号，功能是点击按钮时触发#点击按钮数字加1btn.clicked.connect(lambda : btn.setText(str(int(btn.text()) + 1)))window.show()sys.exit(app.exec_())