# 实时记录窗口的大小、位置、标题# pip install -i https://pypi.tuna.tsinghua.edu.cn/simple xxxx # 打包命令 pyinstaller xxxxxxxx --onefile --icon=xxxxxxxxxx from PyQt5.Qt import *import sysapp=QApplication(sys.argv)window=QWidget()#设置一个按钮，名称是‘显示窗口的信息’btn=QPushButton(window)btn.setText("显示窗口的信息")btn.move(50,50)#点击按钮，显示当前窗口的、标题、大小、位置def show_info():    print(window.windowTitle())    print(window.size())    print(window.pos())btn.clicked.connect(show_info)window.show()sys.exit(app.exec_())