# 重写mybutton# 类方法的使用# pip install -i https://pypi.tuna.tsinghua.edu.cn/simple xxxx # 打包命令 pyinstaller xxxxxxxx --onefile --icon=xxxxxxxxxx from PyQt5.Qt import *import sysclass MyButton(QPushButton):    def __init__(self,parent=None):        super().__init__(parent)        self.setText('按钮')        self.resize(50,50)        self.move(0,100)        #self.setStyleSheet('background-color:red;')        #self.clicked.connect(self.btn_click)    def btn_click(self):        print('按钮被点击了')        self.setText('按钮被点击了')        self.setStyleSheet('background-color:blue;')        self.resize(300,300)        self.move(200,200)    #定义一个快速创建按钮的方法    @classmethod#这句话的作用是把这个方法变成类方法，若不加这句话，下面的btn=MyButton.quick_creat('快速创建按钮',window)会报错    def quick_creat(cls,text,parent=None):        btn=cls(parent)        btn.setText(text)        return btnapp=QApplication(sys.argv)window=QWidget()#快速创建按钮btn=MyButton.quick_creat('快速创建按钮',window)window.show()sys.exit(app.exec_())