# 设置下拉菜单、图标# pip install -i https://pypi.tuna.tsinghua.edu.cn/simple xxxx # 打包命令 pyinstaller xxxxxxxx --onefile --icon=xxxxxxxxxx # from PyQt5.Qt import *# import sys## app = QApplication(sys.argv)# window = QWidget()### window.show()# sys.exit(app.exec_())from PyQt5.Qt import *import sysapp = QApplication(sys.argv)window = QWidget()#创建一个菜单menu = QMenu(window)#创建菜单项item1 = QAction("菜单项1",menu)#设置图标item1.setIcon(QIcon("图标头像.ico"))item2 = QAction("菜单项2",menu)item3 = QAction("菜单项3",menu)#添加菜单项menu.addAction(item1)menu.addAction(item2)menu.addAction(item3)#定义三个功能函数、输出hello1、hello2、hello3def hello1():    print("hello1")def hello2():    print("hello2")def hello3():    print("hello3")# #设置三个菜单项的功能函数# item1.triggered.connect(hello1)# item2.triggered.connect(hello2)# item3.triggered.connect(hello3)#用匿名函数设置三个菜单项的功能函数item1.triggered.connect(lambda :print("hello1"))item2.triggered.connect(lambda :print("hello2"))item3.triggered.connect(lambda :print("hello3"))#添加分割线menu.addSeparator()#添加菜单项4，是子菜单，子菜单里面有三个菜单项item4 = menu.addMenu("子菜单")item5 = QAction("子菜单项1",item4)item6 = QAction("子菜单项2",item4)item7 = QAction("子菜单项3",item4)item4.addAction(item5)item4.addAction(item6)item4.addAction(item7)#创建按钮btn = QPushButton(window)# btn.setText("按钮")btn.move(100,100)btn.setIcon(QIcon("图标头像.ico"))#菜单设置给按钮btn.setMenu(menu)                                           #设置下拉菜单的第三步，把菜单设置给按钮window.show()sys.exit(app.exec_())