# 设置箭头、设置图标、设置图标大小、设置提示文字、设置文字位置# 设置按钮变大效果# pip install -i https://pypi.tuna.tsinghua.edu.cn/simple xxxx # 打包命令 pyinstaller xxxxxxxx --onefile --icon=xxxxxxxxxx from PyQt5.Qt import *import sys#app = QApplication(sys.argv)window = QWidget()#创建一个toolbutton对象tb=QToolButton(window)tb.setText("工具")tb.move(100,100)#设置图标icon=QIcon("图标头像.ico")tb.setIcon(icon)tb.setIconSize(QSize(50,50))#QSize是一个类，不是函数，功能是设置图标大小，setQSize是一个函数，功能是设置窗口大小tb.setToolTip("这是一个工具按钮")#在图标右边显示文字，# tb.setToolButtonStyle(Qt.ToolButtonTextBesideIcon)#在图标下面显示文字# tb.setToolButtonStyle(Qt.ToolButtonTextUnderIcon)#在图标上面显示文字# tb.setToolButtonStyle(Qt.ToolButtonTextUnderIcon)#在图标左面面显示文字# tb.setToolButtonStyle(Qt.ToolButtonTextUnderIcon)#设置四种箭头tb.setArrowType(Qt.UpArrow)# tb.setArrowType(Qt.DownArrow)# tb.setArrowType(Qt.LeftArrow)# tb.setArrowType(Qt.RightArrow)#鼠标移动到按钮上时，增加一个效果，按钮变大tb.setAutoRaise(True)window.show()sys.exit(app.exec_())