# 设置两组分别互斥# 给按钮分组# pip install -i https://pypi.tuna.tsinghua.edu.cn/simple xxxx # 打包命令 pyinstaller xxxxxxxx --onefile --icon=xxxxxxxxxx from PyQt5.Qt import *import sys#app = QApplication(sys.argv)window = QWidget()#创建四个radioButtonrb1 = QRadioButton("男",window)rb2 = QRadioButton("女",window)rb3 = QRadioButton("保密",window)rb4 = QRadioButton("未知",window)#两排两列rb1.move(100,100)rb2.move(100,150)rb3.move(200,100)rb4.move(200,150)#将rb1和rb2放到一组group = QButtonGroup(window)group.addButton(rb1)group.addButton(rb2)#将rb3和rb4放到一组group2 = QButtonGroup(window)group2.addButton(rb3)group2.addButton(rb4)#rb1和rb2互斥group.setExclusive(True)#rb3和rb4互斥group2.setExclusive(True)#定义一个槽函数def cao():    #某个按钮被选中时，返回按钮的名称    print(group.checkedButton().text())    print(group2.checkedButton().text())#给按钮组添加信号group.buttonClicked.connect(cao)group2.buttonClicked.connect(cao)window.show()sys.exit(app.exec_())