# 切换密码的形式# pip install -i https://pypi.tuna.tsinghua.edu.cn/simple xxxx # 打包命令 pyinstaller xxxxxxxx --onefile --icon=xxxxxxxxxx from PyQt5.Qt import *import sys#app = QApplication(sys.argv)window = QWidget()#插入一个文本输入框le = QLineEdit(window)le.move(100,100)le.setPlaceholderText("请输入内容")#插入一个接收输入框输入得内容的按钮btn = QPushButton(window)btn.move(100,150)btn.setText("按钮")#让输入的内容是密码形式le.setEchoMode(QLineEdit.Password)#按钮切换输入框的输入形式def changeEchoMode():    if le.echoMode() == QLineEdit.Normal:        le.setEchoMode(QLineEdit.Password)    else:        le.setEchoMode(QLineEdit.Normal)btn.clicked.connect(changeEchoMode)btn2 = QPushButton(window)btn2.move(100,200)btn2.setText("按钮2")#btn2是清空输入框的内容def clearText():    le.clear()btn2.clicked.connect(clearText)window.show()sys.exit(app.exec_())