# 请输入这个程序的功能 # pip install -i https://pypi.tuna.tsinghua.edu.cn/simple xxxx # 打包命令 pyinstaller xxxxxxxx --onefile --icon=xxxxxxxxxx #from PyQt5.Qt import *import sys#app = QApplication(sys.argv)window = QWidget()#创建两个文本框，一个输入用户名、一个输入密码le1 = QLineEdit(window)le1.move(100,100)le1.setPlaceholderText("请输入用户名")le2 = QLineEdit(window)le2.move(100,150)le2.setPlaceholderText("请输入密码")le2.setEchoMode(QLineEdit.Password)#创建一个登陆按钮，点击按钮，判断用户名和密码是否正确btn = QPushButton(window)btn.move(100,200)btn.setText("登陆")#用户名联想功能completer = QCompleter(["admin","root","guest","user","fangzhongyuan1"],le1)le1.setCompleter(completer)#admin的密码是123456，自动填充密码def completeText(text):    #判断用户名是否是admin    if text == "admin":        le2.setText("123456")    else:        le2.clear()le1.textChanged.connect(completeText)window.show()sys.exit(app.exec_())