# 获取子控件，并设置样式，例如改变颜色，大小等# pip install -i https://pypi.tuna.tsinghua.edu.cn/simple xxxx # 打包命令 pyinstaller xxxxxxxx --onefile --icon=xxxxxxxxxx import sysfrom PyQt5.Qt import *app = QApplication(sys.argv)window_1 = QWidget()#设置窗口的大小window_1.resize(500,500)window_2 = QWidget()#设置成紫色window_2.setStyleSheet("background-color:purple")window_2.resize(100,200)#设置window1是window2的父控件window_2.setParent(window_1)#window_2只能在window_1中显示#在window_1中添加3个标签和3个按钮label_1 = QLabel(window_1)label_1.setText("标签1")label_1.move(400,100)label_2 = QLabel(window_1)label_2.setText("标签2")label_2.move(400,200)label_3 = QLabel(window_1)label_3.setText("标签3")label_3.move(400,300)button_1 = QPushButton(window_1)button_1.setText("按钮1")button_1.move(100,100)button_2 = QPushButton(window_1)button_2.setText("按钮2")button_2.move(200,200)button_3 = QPushButton(window_1)button_3.setText("按钮3")button_3.move(300,300)window_2.show()window_1.show()#获取window_1中所有的Label控件label_list = window_1.findChildren(QLabel)print(label_list)#让window_1的所有标签都变成红色for label in label_list:    label.setStyleSheet("background-color:red")#单独设置window_1中的标签2的颜色label_2.setStyleSheet("background-color:yellow")#获取window_1中所有的按钮控件button_list = window_1.findChildren(QPushButton)sys.exit(app.exec_())