# 学习如何给窗口绑定槽函数，如何取消绑定，如何给窗口设置标题# pip install -i https://pypi.tuna.tsinghua.edu.cn/simple xxxx# 打包命令 pyinstaller xxxxxxxx --onefile --icon=xxxxxxxxxximport sysfrom PyQt5.Qt import *#定义一个功能，打印hello world，点击按钮1时触发def print_hello():    print("hello world")app = QApplication(sys.argv)window_1 = QWidget()#设置窗口的大小window_1.resize(500,500)window_2 = QWidget()#设置成紫色window_2.setStyleSheet("background-color:purple")window_2.resize(100,200)#设置window1是window2的父控件window_2.setParent(window_1)#window_2只能在window_1中显示#在window_1中添加3个标签和3个按钮label_1 = QLabel(window_1)label_1.setText("标签1")label_1.move(400,100)label_2 = QLabel(window_1)label_2.setText("标签2")label_2.move(400,200)label_3 = QLabel(window_1)label_3.setText("标签3")label_3.move(400,300)button_1 = QPushButton(window_1)button_1.setText("按钮1")button_1.move(100,100)button_2 = QPushButton(window_1)button_2.setText("按钮2")button_2.move(200,200)button_3 = QPushButton(window_1)button_3.setText("按钮3")button_3.move(300,300)window_2.show()window_1.show()#给按钮1绑定一个槽函数button_1.clicked.connect(print_hello)#点击按钮没有打印出来def linshi():    #取消绑定    #window_1.windowTitleChanged.disconnect(linshi)    #临时取消绑定    window_1.blockSignals(True)#这句代码的意思是，当window_1的标题发生改变时，就不会触发linshi函数    print('标题发生了改变')    #打印window_1的标题    print(window_1.windowTitle())    #给新标题加上一个前缀    new_title = "这是前缀：" + window_1.windowTitle()    #设置新的标题    window_1.setWindowTitle(new_title)    print(new_title)    window_1.blockSignals(False)    #重新绑定    #window_1.windowTitleChanged.connect(linshi)#将标签、按钮、窗口都设置成window_1的子控件label_1.setParent(window_1)label_2.setParent(window_1)label_3.setParent(window_1)button_1.setParent(window_1)button_2.setParent(window_1)button_3.setParent(window_1)#按钮、标签中间垂直排列button_1.move(100,100)button_2.move(100,200)button_3.move(100,300)label_1.move(400,100)label_2.move(400,200)label_3.move(400,300)#获取所有标签控件for i in window_1.findChildren(QLabel):    print(i.text())#给window_1绑定一个槽函数window_1.windowTitleChanged.connect(linshi)#这句代码的意思是，当window_1的标题发生改变时，就会触发linshi函数#改变window_1的标题window_1.setWindowTitle("第一次改变标题")sys.exit(app.exec_())