# 获取所有标签控件，遍历标签控件，查找标签都继承自谁# pip install -i https://pypi.tuna.tsinghua.edu.cn/simple xxxx# 打包命令 pyinstaller xxxxxxxx --onefile --icon=xxxxxxxxxximport sysfrom PyQt5.Qt import *#定义一个功能，打印hello world，点击按钮1时触发def print_hello():    print("hello world")app = QApplication(sys.argv)window_1 = QWidget()#设置窗口的大小window_1.resize(500,500)window_2 = QWidget()#设置成紫色window_2.setStyleSheet("background-color:purple")window_2.resize(100,200)#设置window1是window2的父控件window_2.setParent(window_1)#window_2只能在window_1中显示#在window_1中添加3个标签和3个按钮label_1 = QLabel(window_1)label_1.setText("标签1")label_2 = QLabel(window_1)label_2.setText("标签2")label_3 = QLabel(window_1)label_3.setText("标签3")button_1 = QPushButton(window_1)button_1.setText("按钮1")button_2 = QPushButton(window_1)button_2.setText("按钮2")button_3 = QPushButton(window_1)button_3.setText("按钮3")window_2.show()window_1.show()#将标签、按钮、窗口都设置成window_1的子控件label_1.setParent(window_1)label_2.setParent(window_1)label_3.setParent(window_1)button_1.setParent(window_1)button_2.setParent(window_1)button_3.setParent(window_1)#按钮、标签中间垂直排列button_1.move(100,100)button_2.move(100,200)button_3.move(100,300)label_1.move(400,100)label_2.move(400,200)label_3.move(400,300)#获取所有标签控件label_list = window_1.findChildren(QLabel)print(label_list)#查找标签都继承自谁print(label_list[0].inherits("QLabel"))sys.exit(app.exec_())